import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'



function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    let room = []
    
    
    const getMessages = ()=>{
        
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            
                snapshot.docs.map(doc => 
                    db.collection('posts').doc(doc.id).collection('rooms')
                    .orderBy('timestamp','asc')
                    .onSnapshot(snapshot => 
                        
                        snapshot.docs.map( 
                            doc => {
                            
                            if(
                                activeUser 
                                && doc.data() 
                                && (doc.data().receiver.toLowerCase() 
                                === 
                                activeUser.toLowerCase())
                            ){
                                
                                room.unshift(doc.data())
                                setMessages([...room])
                                return [doc.data(),...room]
                                
                                                        
                            }else{
                                return null
                            }
                        }
                        )
                        
                        
                        )
                        
                   
                    
                    
                    
                )

            
        )
        

    }

useEffect(()=>{

    getMessages();
    
    return ()=> setMessages([])
    
},[activeUser])
    



    return (
        <div className='messages_container'>
           <div>{messages.length}</div>
            
            {messages.map((msg,id)=>{
                return(
                    <Fade top key={id}>
                    <div>
                    
                        <h3>{msg ? msg.sender : null}</h3>
                        <p>{msg ? msg.message : null}</p>
                    </div>
                    </Fade>
                )
            })}
            
               
           
           


           
        </div>
    )
}

export default ClientRoom
