import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'



function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([]);
    const [ids,setIds] = useState([]);
const content = [];

const getData = (id)=>{
    db.collection('posts').doc(id).collection('rooms').orderBy('timestamp','asc')
    .onSnapshot (snp => snp.docs.map(doc2 => {
        if(doc2.data().receiver.toLowerCase() === activeUser.toLowerCase()){
            return 
        }else{
            return null
        }
    }
        
        ) )

}
    
const getIds = ()=>{
    if(activeUser) {
        db.collection('posts')
        .orderBy('timestamp','asc')
        .onSnapshot ( snapshot => 
            setIds(snapshot.docs.map(doc1 => getData(doc1.id))) )
    }
    
    
}    

useEffect(()=>{
    getIds()
},[activeUser])
    

    



    return (
        <div className='messages_container'>
           <div>{messages.length}</div>
           <button onClick={(e)=>{
               e.preventDefault();
               console.log(ids)
           }}>Click</button>
            
            {messages.map((msg,id)=>{
                return(
                    <Fade top key={id}>
                    <div>
                    
                        <h3>{msg ? msg.sender : null}</h3>
                        <p>{msg ? msg.message : null}</p>
                    </div>
                    </Fade>
                )
            })}
            
               
           
           


           
        </div>
    )
}

export default ClientRoom
