import React,{useState,useContext} from 'react';
import './addPost.css';
import {SideBarContext} from '../../contexts/SideBarContext';
import {storage,db} from '../../Firebase';

import { Fade } from 'react-reveal';

function AddPost() {

    const [imagesSelected,setImagesSelected] = useState([]);
    // const [inputValue,setInputValue] = useState('');

    const {value2} = useContext(SideBarContext);
    
    const [activeUser,setActiveUser] = value2;

    const [title,setTitle] = useState('');
    const [price,setPrice] = useState('');
    const [category,setCategory] = useState('');
    const [place,setPlace] = useState('');


    


    const hundleChange = (e) =>{

        let i =0;
        if(e.target.value !== ''){
            // setInputValue(e.target.files[i])
            setImagesSelected([e.target.files[i],...imagesSelected]);
            i++;
            
        }
    }

    const hundleSelect = ()=> {
        let input = document.getElementById('file');
        input.click();      
    }

    const saveArticle = (e)=>{
        e.preventDefault();
        imagesSelected.forEach((image,index)=>{
            const uploadTask = storage.ref(`images/${image.name}`).put(image);
            uploadTask.on(
                "state_changed",
                () => {
                    storage
                    .ref('images')
                    .child(image.name)
                    .getDownloadURL()
                    .then(url => {
                        db.collection('posts').add({
                            imagesUrl : [url,...im],
                            price : price,
                            productName : title,
                            username : activeUser,
                            place : place,
                            category : category

                        })
                    })

                }

            )
        })
        


        
        
       
        setImagesSelected([]);
        

    }

    

    return (
        <Fade right>
        <div className="container">
        
        <input type="file" className='file'
        onChange={hundleChange}
        
          name="" id="file" />

        <div className='add_container'>
            <h3>Add a Post</h3>

            <div className="data_container"
            onClick={hundleSelect}
            >
                    <i className="fas fa-folder-plus"></i>
                    <h4>Add images</h4>

            </div>
            <div className="details">
                <form action="">
                    <div className="title">
                        <input type="text" placeholder='Title' name="" id="" value={title}
                            onChange={(e)=> setTitle(e.target.value)}
                        />
                    </div>
                    <div className="price">
                    <input type="text" placeholder='Price' name="" id="" 
                         value={price}
                        onChange={(e)=> setPrice(e.target.value)}
                    />

                    </div>
                    <div className="category">
                    <input type="text" placeholder='Category' name="" id="" 
                         value={category}
                        onChange={(e)=> setCategory(e.target.value)}
                    />
                        <i className="fas fa-caret-down"></i>             
                    </div>
                    <div className="place">
                        <input type="text" placeholder='Place' name="" id=""
                        value={place}
                        onChange={(e)=> setPlace(e.target.value)}
                         />
                        <i className="fas fa-map-marker-alt"></i>
                    </div>

                    <div className="save_article">
                        <button type='submit' onClick={saveArticle}>Publish</button>
                    </div>

                    
                </form>
            </div>

            
            
        </div>

        </div>
        </Fade>
        
    )
}

export default AddPost
