import React,{useState} from 'react';
import './addPost.css'

function AddPost() {

    const [imagesSelected,setImagesSelected] = useState([]);
    const [inputValue,setInputValue] = useState('');

    const hundleSelect = ()=> {
        let input = document.getElementById('file');
        input.click();
        if(inputValue !== ''){
            setImagesSelected([...imagesSelected,inputValue]);
            setInputValue('');

        }
        

    }
    

    return (
        <div className="container">
        
        <button onClick={()=> console.log(imagesSelected)}>
            Show the array
        </button>
        <input type="file" className='file' value={inputValue}
        onChange={(e)=> setInputValue(e.target.value)}
        
          name="" id="file" />

        <div className='add_container' 
        onClick={hundleSelect}
        >
            <h3>Add a Post</h3>

            <div className="data_container">
                    <i className="fas fa-folder-plus"></i>
                    <h4>Add images</h4>

            </div>
            <div className="details">
                <form action="">
                <div className="title">
                    <input type="text" placeholder='Title' name="" id="" />
                </div>
                <div className="price">
                <input type="text" placeholder='Price' name="" id="" />

                </div>
                <div className="category">
                    <span>Category</span>
                    <i className="fas fa-caret-down"></i>
                    
                </div>
                <div className="place">
                    <input type="text" placeholder='Place' name="" id="" />
                    <i className="fas fa-map-marker-alt"></i>
                </div>

                <div className="save_article">
                    <button type='submit' onClick={saveArc}>Publish</button>
                </div>
                </form>
            </div>

            
            
        </div>

        </div>
        
    )
}

export default AddPost
