import React from 'react';
import './header.css';
import {useState} from 'react';
import { Link } from 'react-router-dom';

function Header() {
    const [open,setOpen] = useState(false);
    const [text,setText] = useState('');
    const [inOpen,setIn] = useState(null);
    const [upOpen,setUp] = useState(null);

    const login = (e)=> {
        e.preventDefault();
        console.log(text);
        setText('');
    }
    
    return (
        <div className='header'>
            <div className="header__container">
                <div className="logo">
                    <Link to='/' className='logo'>
                        <i className="fas fa-store"></i>
                        <h3>Market place</h3>
                    </Link>
                    
                </div>
                <div className="search">
                    <form action="">

                    <input 
                        type="text" 
                        placeholder='Search an item ...'
                        value={text}
                        onChange={(e)=>setText(e.target.value)}
                     />
                     <button type="submit" onClick={login}>
                     <i className="fas fa-search"></i>

                     </button>
                    

                    </form>
                    
                </div>
                <div className="auth">
                    <div className={{inOpen} === false ?  "link sign_in" : "link on sign_in" }
                    onClick={ ()=> {
                        setIn(true);
                        setUp(false);
                    }}
                    
                    >
                        <h3>
                            <Link to={'/SignIn'}>
                                Sign In
                            </Link>
                        </h3>
                    </div>
                    
                    <div className={{upOpen} === false ?  "link sign_up" : "link on sign_up" }
                     onClick={ ()=> {
                        setIn(false);
                        setUp(true);
                    }}
                    >
                        <h3>
                            <Link to={'/SignUp'}>
                                Sign up
                            </Link>
                        </h3>
                    </div>

                    <i className="fas fa-bars" onClick={() => setOpen(!open)}></i>
                </div>
                {open === true ? (
                    <div className="auth-mobile">
                    <div className="signIn"
                        onClick={()=> setOpen(false)}
                     >
                        <Link to='/SignIn'>
                            Sign In
                        </Link>
                        
                    </div>
                    <div className="signUp"
                        onClick={()=> setOpen(false)}
                    >
                        <Link to='/SignUp'>
                            Sign Up
                        </Link>
                    </div>
                </div>

                ): null}
                
            </div>
            
        </div>
    )
}

export default Header
