import React,{useState,useEffect} from 'react';
import './postDetail.css';
import { useParams } from 'react-router-dom';
import {db} from '../../Firebase';
import Zoom from 'react-reveal'




function PostDetail() {
    let {id} = useParams();
    const [postDetail,setPostDetail] = useState({});
    const [show,setShow] = useState(false)

    useEffect(()=>{
        db.collection('posts')
        .doc(id).onSnapshot( snapshot => {
            setPostDetail(snapshot.data())
            

            
        });
        // setPostDetail({})
        

        

    },[id]);
    
    
    if(Object.keys(postDetail).length !== 0){
        // postDetail.imagesUrl.map(url => console.log(url))
        // setShow(true)

    } 

    


    return (
        <div className='post_detail_container'>
        {Object.keys(postDetail).length !== 0  ? (
            postDetail.imagesUrl.map((url,index) => {
                return (

                    <div className='post_img' key={index}>
                    <Zoom>
                    <img src={url} alt="" />

                    </Zoom>
                        
                    </div>
                )
            })
               
           

        ):null}
            
            
        </div>
    )
}

export default PostDetail
