                db.collection('posts').doc(getId[i])
                .collection('rooms').orderBy('timestamp','asc')
                .onSnapshot(snapshot => setGetMessages(snapshot.docs.map(doc => doc.data())))
            }
            console.log(getMessages)
        }

    }
    


    
    
    

