import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import { scryRenderedComponentsWithType } from 'react-dom/cjs/react-dom-test-utils.production.min';

function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [elements,setElements] = useState([])
    const [rooms,setRooms] = useState([])
    const [getId,setGetId] = useState([])
    const [valid,setValid] =useState(false)

let messages = []

    const getElements = ()=> {
        db.collection('posts').orderBy('timestamp','asc').onSnapshot(snapshot => {
            setElements(snapshot.docs.map(doc=> doc.data()))
            setGetId(snapshot.docs.map(doc=> doc.id));
            

            
            
        }
            
            );
    
    }
    const getMessages = (id)=>{
        db.collection('posts')
        .doc(id)
        .collection('rooms')
        .orderBy('timestamp','asc')
        .onSnapshot(snapshot => snapshot.docs.map(doc => messages.unshift(doc.data())))

    }
    

    useEffect(()=>{
        getElements()
        console.log("first useEffect")
        
        return () => setElements([])

    },[])

    useEffect(()=>{
        
        if(getId.length === elements.length){
          let i = 1;
          if(i> 0){

            getId.map((id) => getMessages(id))
            i++;
            if(messages.length !== 0) {
                i=-1;
            }

          }
           
    
        }
        
    },[getId])

    

    return (
        <div className='messages_container'>
           <button onClick={(e)=>{
               e.preventDefault()
               
            console.log(messages)
           }}>click</button> 
               
            <div>
                {messages.length}
            </div>
               
           
           


           
        </div>
    )
}

export default ClientRoom
