import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';


function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    
    const getMessages = async()=>{
        const response1 = db.collection('posts').orderBy('timestamp','asc')
        const data1 = await response1.get();
        data1.docs.forEach((item) => {
            const response2 = db.collection('posts').doc(item.id).collection('rooms').orderBy('timestamp','asc');
            const data2 = response2.get();
            data2.docs.map(msg =>{
                setMessages([msg.data(),...messages])
