    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])

    useEffect(()=>{
        db.collection('rooms').onSnapshot(snapshot => setMessages(snapshot.docs.map(doc=> doc.data())))

    },[])

    

