import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';

function ClientRoom() {
    const [getIds,setGetIds] = useState([])
    const [getMessages,setGetMessages] = useState([]);
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [message,setMessage] = useState('');

    useEffect(()=>{
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot =>{
            setGetIds(snapshot.docs.map(doc => {
                // console.log(doc.data());
                db.collection('posts').doc(doc.id)
                .collection('rooms')
                .onSnapshot(snapshot => {
                    return setGetMessages(snapshot.docs.map(doc => doc.data()))
                })
                
                
                
            }))
        })
    },[])
    
    

    return (
        <div className='container'>
           <button 
           onClick={(e)=> {
               e.preventDefault();
               console.log(getIds)
           }}
           > click </button>
        </div>
    )
}

export default ClientRoom
