import React,{useState,useEffect,useContext} from 'react';
import './room.css';
import { useParams } from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Zoom from 'react-reveal/Zoom';
import firebase from 'firebase/compat';



function Room() {
    const [sellerName,setSellerName] = useState('');
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [message,setMessage] = useState('');
    const [getMessages,setGetMessages] = useState([]);
    
    

    let {id} = useParams();

    // let content = [] 
   
    useEffect(()=>{
        db.collection('posts')
        .doc(id).onSnapshot( snapshot => {
            setSellerName(snapshot.data().username)    
        });

        db.collection('posts').doc(id).collection('rooms')
        .orderBy('timestamp','asc')
        .onSnapshot(snapshot => snapshot.docs.map(doc => {
                if(activeUser && sellerName){
                    if( (doc.data().sender.toLowerCase() === sellerName.toLowerCase() 
                    && doc.data().seller.toLowerCase() === sellerName.toLowerCase()  
                    && doc.data().receiver.toLowerCase()  === activeUser.toLowerCase()  )
                    ||
                    (doc.data().sender.toLowerCase()  === activeUser.toLowerCase()  
                    && doc.data().seller.toLowerCase()  === sellerName.toLowerCase()  
                    && doc.data().receiver.toLowerCase()  === sellerName.toLowerCase() )
                    
                    ){
                        setGetMessages([doc.data(),...getMessages])
                    }
                }
                return getMessages
        }))
        
        

        return () => {
            setSellerName('');
            setGetMessages({})
        }

    },[id,activeUser,sellerName])

    const hundleChange = (e)=> {
        // e.preventDefault();
        setMessage(e.target.value)

    }
    const sendMessage = (e)=>{
        e.preventDefault();
        if(message !==''){
            db.collection('posts')
            .doc(id)
            .collection('rooms').add({
                message,
                sender : activeUser,
                seller : sellerName,
                receiver : sellerName,
                timestamp : firebase.firestore.FieldValue.serverTimestamp()
            })
        }
        setMessage('');
    }
   
    return (
        <div className='container'>
        <Zoom>
            <div className="room_container">
                <div className="header">
                    <div>
                        <Avatar 
                                alt={activeUser === sellerName ? "client" : sellerName }
                                className='avatar'
                                src="/static/images/avatar/1.jpg"
                                onClick={ ()=> { 
                                    
                                    
                                    }}
                        />
                    </div>
                    <h3>{activeUser === sellerName ? "client" : sellerName }</h3>
                    
                </div>
                <div className="chat">
                    {getMessages.length !== 0 ? (
                        getMessages.map((msg,index) => {
                           
                            return(
                            <div 
                            className={msg.name === activeUser ? "message_receiver" : "message"}
                            key={index}
                            >   <h4>{msg.name}</h4>
                                <p>{msg.message}</p>
                                <span>
                                {new Date(
                                msg.timestamp?.toDate()
                                ).toUTCString()
                                }
                                </span>

                            </div>

                        )
                        

                    } )
                        

                    ):null}
                                
                </div>
                <div className="text-field">
                <form action="">
                <input type="text" name="" id="" 
                    placeholder={activeUser !== sellerName ? `Type a message to ${sellerName}` : 'Type a message to the Client'} 
                    value={message}
                    onChange={hundleChange}
                    />
                    <button type='submit' onClick={sendMessage}>
                     <i className="far fa-paper-plane"></i>

                    </button>
                    
                </form>
                    
                </div>

            </div>
        </Zoom>
        </div>
    )
}

export default Room
