import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'
import { scryRenderedDOMComponentsWithClass } from 'react-dom/cjs/react-dom-test-utils.development';


function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    const room = []
    
    const getMessages = ()=>{
        
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            
                snapshot.docs.map(doc => {
                    db.collection('posts').doc(doc.id).collection('rooms')
                    .orderBy('timestamp','asc')
                    .onSnapshot(snapshot => 
                        setMessages(
                        snapshot.docs.map( 
                            doc=> {
                            if(activeUser&& doc.data() && (doc.data().receiver.toLowerCase() === activeUser.toLowerCase()))
                            {
                                
                                room.unshift(doc.data())
                                
                                
                                
                                return [...room]
                            }else{
                                return [...room];
                            }
                        }
                        ))
                        
                        )
                        
                    
                    return messages
                    
                    
                })

            
        )

    }

useEffect(()=>{
    
    getMessages();
    
