import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';

function ClientRoom() {
    const [getRooms,setGetRooms] = useState([])
    const [getId,setGetId] = useState([])
    const [getMessages,setGetMessages] = useState([]);
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [message,setMessage] = useState('');
    const [state,setState] = useState(false)

    let myMessages = [];
    let rooms =[]

    useEffect(()=>{
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => setGetId(snapshot.docs.map(doc => doc.id)))
        // setState(true)

    },[])

   useEffect(()=>{
    for(let i=0 ; i< getId.length ; i++){
        
        db.collection('posts').doc(getId[i])
        .collection('rooms').orderBy('timestamp','asc')
        .onSnapshot( snapshot =>
          snapshot.docs.map(doc => myMessages.unshift(doc.data()) )
            )
            
        
       
    }
    setState(true)
},[rooms])
    
    


    const show = ()=>{
        myMessages.map(msg => {
            
            if(msg.receiver.toLowerCase() === activeUser.toLowerCase()){
                rooms.unshift(msg);
        
            }
            return rooms
        })
        
        
        
    }
    useEffect(()=>{
        show()
    },[state])
   
    
    

    return (
        <div className='container'>
           {/* {getMessages.map((msg,index)=>{
               return(
                   <div key={index}>
                   {msg.sender} <br />
                   {msg.message}

                   </div>
               )
           })} */}
           <button onClick={(e)=>{
               e.preventDefault();
            //    show()
               console.log(rooms)
               
           }}> click</button>
        </div>
    )
}

export default ClientRoom
