import React,{useState,useEffect,useContext} from 'react';
import './room.css';
import { useParams } from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Zoom from 'react-reveal/Zoom';



function Room() {
    const [sellerName,setSellerName] = useState('');
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;

    let {id} = useParams();

    useEffect(()=>{
        db.collection('posts')
        .doc(id).onSnapshot( snapshot => {
            setSellerName(snapshot.data().username)
            
            
        });

    },[id])

   
    return (
        <div className='container'>
        <Zoom>
            <div className="room_container">
                <div className="header">
                    <div>
                        <Avatar 
                                alt={sellerName} 
                                className='avatar'
                                src="/static/images/avatar/1.jpg"
                                onClick={ ()=> { 
                                    
                                    
                                    }}
                        />
                    </div>
                    <h3>{sellerName}</h3>
                    
                </div>
                <div className="chat">
                                
                </div>
                <div className="text-field">
                    <input type="text" name="" id="" placeholder={`Type a message to ${sellerName}`} />
                    
                </div>

            </div>
        </Zoom>
        </div>
    )
}

export default Room
