import React,{useState,useEffect,useContext} from 'react';
import './room.css';
import { useParams } from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Zoom from 'react-reveal/Zoom';
import firebase from 'firebase/compat';



function Room() {
    const [sellerName,setSellerName] = useState('');
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [message,setMessage] = useState('');
    const [getMessages,setGetMessages] = useState({});

    let {id} = useParams();

    useEffect(()=>{
        db.collection('posts')
        .doc(id).onSnapshot( snapshot => {
            setSellerName(snapshot.data().username)
            
            
        });
        db.collection('posts').doc(id).collection


    },[id])

    const hundleChange = (e)=> {
        // e.preventDefault();
        setMessage(e.target.value)

    }
    const sendMessage = (e)=>{
        e.preventDefault();
        if(message !==''){
            db.collection('posts')
            .doc(id).collection('rooms').add({
                message,
                username : activeUser,
                timestamp : firebase.firestore.FieldValue.serverTimestamp()
            })
        }
        setMessage('');

        db.collection

    }
   
    return (
        <div className='container'>
        <Zoom>
            <div className="room_container">
                <div className="header">
                    <div>
                        <Avatar 
                                alt={sellerName} 
                                className='avatar'
                                src="/static/images/avatar/1.jpg"
                                onClick={ ()=> { 
                                    
                                    
                                    }}
                        />
                    </div>
                    <h3>{sellerName}</h3>
                    
                </div>
                <div className="chat">
                    <div className={sellerName === activeUser ? "message" : "message_receiver"}>
                        <p>Hello</p>
                        <span>22:00</span>

                    </div>
                                
                </div>
                <div className="text-field">
                <form action="">
                <input type="text" name="" id="" 
                    placeholder={`Type a message to ${sellerName}`} 
                    value={message}
                    onChange={hundleChange}
                    />
                    <button type='submit' onClick={sendMessage}>
                     <i className="far fa-paper-plane"></i>

                    </button>
                    
                </form>
                    
                </div>

            </div>
        </Zoom>
        </div>
    )
}

export default Room
