import React, { useEffect, useState } from 'react';
import './posts.css';
import {db} from '../../Firebase';
import Zoom from 'react-reveal/Zoom';

function Posts() {
    const [posts,setPosts] = useState([]);

    useEffect(()=>{
        data();
        return () => setPosts([])
        

    },[])

    const data = ()=> {
        db.collection('posts').onSnapshot( snapshot => {
            setPosts( snapshot.docs.map(doc => ({
                id:doc.id,
                post : doc.data()
            }))

            )
        });
    }

    return (
        <Zoom>
        <div className='posts_container'>
        
        
        {posts.map( ({id , post})=>{
            return(
                
                <div className="posts" key={id}>
                    <img src={post.imagesUrl[0]} alt="" />
                    <h4>{post.productName}</h4>
                    <h3>{post.price}</h3>
                </div>
                
            )
        }
            
        

        )}
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
  <path fill="#0099ff" fill-opacity="1" d="M0,160L48,144C96,128,192,96,288,112C384,128,480,192,576,181.3C672,171,768,85,864,90.7C960,96,1056,192,1152,224C1248,256,1344,224,1392,208L1440,192L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path>
</svg>
            
        </div>
        </Zoom>
        
    )
}

export default Posts
