import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'


function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    const room = []
    
    const getMessages = ()=>{
        
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            snapshot.docs.map(doc => {
                db.collection('posts').doc(doc.id).collection('rooms')
                .orderBy('timestamp','asc')
                .onSnapshot(snapshot => 
                    setMessages(snapshot.docs.map(doc=> {
                        if(activeUser && doc.data().receiver.toLowerCase() === activeUser.toLowerCase()){
                            return doc.data()
                        }else{
                            return null
                        }
                    })) 
                    
                    )
                
                
                
            })
        )

    }

useEffect(()=>{

    getMessages();
    return ()=> setMessages([])
    
},[messages,activeUser])
    

    

    return (
        <div className='messages_container'>
           
            
            {messages.map((msg,id)=>{
                return(
                    <Fade top key={id}>
                    <div>
                        <h3>{msg ? msg.sender : null}</h3>
                        <p>{msg ? msg.message : null}</p>
                    </div>
                    </Fade>
                )
            })}
            
               
           
           


           
        </div>
    )
}

export default ClientRoom
