import React,{useState,useContext,useEffect} from 'react';
import './addPost.css';
import {SideBarContext} from '../../contexts/SideBarContext';
import {storage,db,storageRef} from '../../Firebase';

import { Zoom } from 'react-reveal';

function AddPost() {

    const [imagesSelected,setImagesSelected] = useState([]);
    const [imagesName,setImagesName] = useState([])
    // const [inputValue,setInputValue] = useState('');

    const {value2} = useContext(SideBarContext);
    
    const [activeUser,setActiveUser] = value2;

    const [title,setTitle] = useState('');
    const [price,setPrice] = useState('');
    const [category,setCategory] = useState('');
    const [place,setPlace] = useState('');
    const [urls,setUrls] = useState('')
    const [image,setImage] = useState('')
    const [links,setLinks] = useState()
    
    
    let i = -1;
    
    const hundleSelect = ()=> {
        let input = document.getElementById('file');
        input.click();
        i++;    
    }

    const hundleChange = (e) =>{

        
        if(e.target.value !== ''){
            // setInputValue(e.target.files[i])
            setImagesSelected([e.target.files[i],...imagesSelected]);
            setImagesName([e.target.files[i].name,...imagesName])
            
            
        }
    }
    
    useEffect(() => {
        const fetchImages = async () => {
    
        let result = await storageRef.child('images').listAll();
            let urlPromises = result.items.map((imageRef) => 

                imagesName.forEach((image)=> {
                    if(image === imageRef.name) {
                        console.log("hello");
                        setImage(imageRef.getDownloadURL())
                        imageRef.getDownloadURL()}
                        
                    
                })
                
                
            

            );
        
            return Promise.all(urlPromises);
    
        }
        
        const loadImages = async () => {
            const urls = await fetchImages();
            setUrls(urls);
            
        }
        loadImages();
        }, [imagesName]);


    const saveArticle = async(e)=>{
        e.preventDefault();
        
        

         imagesSelected.map( async (image) => { 
            await storage.ref(`images/${image.name}`).put(image);
            return 1
             
            }); 

        console.log("hello from save function :  ",urls)
        console.log(image)
        
       
        
        
        
        // result.items.map( (item)=>{
        //     if(item.name === 'img-1.jpg') return console.log("img-1 is here");
        //     else return console.log("Noooooo")
            
            
        // });
        
        
        

        
        
        
        
        
        

        setPlace('');
        setTitle('');
        setCategory('');
        setPrice('');
        

    }
    

    

    return (
        
        <Zoom>
        <div className="container">
        
        <input type="file" className='file'
        onChange={hundleChange}
        
          name="" id="file" />

        
            <div className='add_container'>
            <h3>Add a Post</h3>

            <div className="data_container"
            onClick={hundleSelect}
            >
                    <i className="fas fa-folder-plus"></i>
                    <h4>Add images</h4>

            </div>
            <div className="details">
                <form action="">
                    <div className="title">
                        <input type="text" placeholder='Title' name="" id="" value={title}
                            onChange={(e)=> setTitle(e.target.value)}
                        />
                    </div>
                    <div className="price">
                    <input type="text" placeholder='Price' name="" id="" 
                         value={price}
                        onChange={(e)=> setPrice(e.target.value)}
                    />

                    </div>
                    <div className="category">
                    <input type="text" placeholder='Category' name="" id="" 
                         value={category}
                        onChange={(e)=> setCategory(e.target.value)}
                    />
                        <i className="fas fa-caret-down"></i>             
                    </div>
                    <div className="place">
                        <input type="text" placeholder='Place' name="" id=""
                        value={place}
                        onChange={(e)=> setPlace(e.target.value)}
                         />
                        <i className="fas fa-map-marker-alt"></i>
                    </div>

                    <div className="save_article">
                        <button type='submit' onClick={saveArticle}>Publish</button>
                    </div>

                    
                </form>
            </div>

            
            
            </div>
            
        </div>
        
        </Zoom>
        
    )
    }

export default AddPost
