import React,{useState,useEffect,useContext} from 'react';
import './room.css';
import { useParams } from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Zoom from 'react-reveal/Zoom';
import firebase from 'firebase/compat';



function Room() {
    const [sellerName,setSellerName] = useState('');
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [message,setMessage] = useState('');
    const [getMessages,setGetMessages] = useState({});
    const [firstMessage,setFirstMessage] =useState('')
    const [client,setClient] = useState('');

    let {id} = useParams();

    useEffect(()=>{
        db.collection('posts')
        .doc(id).onSnapshot( snapshot => {
            setSellerName(snapshot.data().username)
            
            
        });
        
        db.collection('posts')
        .doc(id)
        .collection('rooms')
        .orderBy('timestamp','asc')
        .onSnapshot(snapshot=>{
            setGetMessages(snapshot.docs.map(doc => doc.data()));
        })

        if(sellerName !== activeUser) {
            db.collection('posts')
            .doc(id).collection('rooms').orderBy('timestamp','desc')
            .onSnapshot(snapshot => {
                setFirstMessage(snapshot.docs.map(doc => 
                    doc.data()[Object.keys(doc.data())[0]]
                    ));
                setClient(firstMessage.toString())
                // console.log(firstMessage)
                
            })
            // setFirstMessage({})
            
        }


       


    },[id,client,activeUser,firstMessage,sellerName])

    const hundleChange = (e)=> {
        // e.preventDefault();
        setMessage(e.target.value)

    }
    const sendMessage = (e)=>{
        e.preventDefault();
        if(message !==''){
            db.collection('posts')
            .doc(id)
            .collection('rooms').add({
                message,
                name : activeUser,
                timestamp : firebase.firestore.FieldValue.serverTimestamp()
            })
        }
        setMessage('');

        db.collection('posts')
        .doc(id)
        .collection('rooms')
        .orderBy('timestamp','asc')
        .onSnapshot(snapshot=>{
            setGetMessages(snapshot.docs.map(doc => doc.data()));
        })

        
        console.log(firstMessage.toString())
    }
   
    return (
        <div className='container'>
        <Zoom>
            <div className="room_container">
                <div className="header">
                    <div>
                        <Avatar 
                                alt={sellerName} 
                                className='avatar'
                                src="/static/images/avatar/1.jpg"
                                onClick={ ()=> { 
                                    
                                    
                                    }}
                        />
                    </div>
                    <h3>{sellerName}</h3>
                    
                </div>
                <div className="chat">
                    {Object.keys(getMessages).length !== 0 ? (
                        getMessages.map((msg,index)=> {
                            return(
                            <div 
                            className={msg.name === activeUser ? "message_receiver" : "message"}
                            key={index}
                            >
                                <p>{msg.message}</p>
                                <span>
                                {new Date(
                                msg.timestamp?.toDate()
                                ).toUTCString()
                                }
                                </span>

                            </div>

                        )
                    } )
                        

                    ):null}
                                
                </div>
                <div className="text-field">
                <form action="">
                <input type="text" name="" id="" 
                    placeholder={`Type a message to ${sellerName}`} 
                    value={message}
                    onChange={hundleChange}
                    />
                    <button type='submit' onClick={sendMessage}>
                     <i className="far fa-paper-plane"></i>

                    </button>
                    
                </form>
                    
                </div>

            </div>
        </Zoom>
        </div>
    )
}

export default Room
