import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';

function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [elements,setElements] = useState([])
    const [rooms,setRooms] = useState([])
    const [getId,setGetId] = useState([])

    const getElements = ()=> {
        db.collection('posts').orderBy('timestamp','asc').onSnapshot(snapshot => {
            setElements(snapshot.docs.map(doc=> doc.data()))
            setGetId(snapshot.docs.map(doc=> doc.id))
        }
            
            );
    
    }
    useEffect(()=>{
        getElements()
        return () => setElements([])
    

    },[])
let messages = []

useEffect(()=>{
    if(getId.length !==0){
        getId.forEach(id => {
            db.collection('posts').doc(id).collection('rooms').orderBy('timestamp','asc')
            .onSnapshot(snapshot => {
                snapshot.docs.map(doc=> 
                   messages.unshift(doc.data())      )
                    setRooms(messages);

            }
             )
                
        })
        
    }  
    // console.log("hello")
   
},[getId])

useEffect(()=> {
    setRooms(messages);
    console.log(rooms)

},[getId])

    return (
        <div className='messages_container'>
           <button onClick={(e)=>{
               e.preventDefault()
               
               console.log(rooms)
           }}>click</button>

           
        </div>
    )
}

export default ClientRoom
