import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'


function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    const room = []
    
    const getMessages = ()=>{
        
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            snapshot.docs.map(doc => {
                db.collection('posts').doc(doc.id).collection('rooms')
                .orderBy('timestamp','asc')
                .onSnapshot(snapshot => 
                snapshot.docs.map((doc) => 
                    { 
                        
                        
                        if(activeUser){
                            if(doc.data().receiver.toLowerCase() === activeUser.toLowerCase()){
                                room.unshift(doc.data())      
                                setMessages([...room])
                            }
                            
                        }

                        return messages
                            

                        
                       
                    }))
                
                return ()=> messages
                
            })
        )

    }

useEffect(()=>{

    getMessages();
    return ()=> setMessages([])
    
},[activeUser])
    

    

    return (
        <div className='messages_container'>
           
            
            {messages.map((msg,id)=>{
                return(
                    <Fade top key={id}>
                    <div key={id}>
                        <h3>{msg.sender}</h3>
                        <p>{msg.message}</p>
                    </div>
                    </Fade>
                )
            })}
            
               
           
           


           
        </div>
    )
}

export default ClientRoom
