import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';
import Fade from 'react-reveal/Fade'



function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([]);
    

    const getData = ()=>{
       
        db.collection('chats')
        .orderBy('timestamp','desc')
        .onSnapshot(snapshot => 
            setMessages(snapshot.docs.map(
                doc => {
                    if(activeUser&& doc.data() && (doc.data().receiver.toLowerCase() === activeUser.toLowerCase())){
                       
                        return doc.data()
                    }
                    else{
                        return null
                    }
                } 
                
        )))
    }
    useEffect(()=>{
        getData()

    },[activeUser])


    return (
        <div className='messages_container'>
           <button>clcick</button>
            
            {messages && messages.map((msg,id)=>{
                return(
                    <Fade top key={id}>
                    <div>
                    
                        <h3>{msg ? msg.sender : null}</h3>
                        <p>{msg ? msg.message : null}</p>
                    </div>
                    </Fade>
                )
            })}
            
               
           
           


           
        </div>
    )
}

export default ClientRoom
