import React,{useState,useEffect,useContext} from 'react';
import './myPost.css';
import {db} from '../../Firebase';
import Zoom from 'react-reveal/Zoom';
import Slider from "react-slick";
import {Link} from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import cap from '../../images/cap-1.jpg'
import cap2 from '../../images/cap-2.jpg'
import {SideBarContext} from '../../contexts/SideBarContext';



function MyPosts() {
    const [myPosts,setMyPosts] = useState([]);
    const [on,setOn] = useState(false);
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;

let date = 0;
let item = '';

    useEffect(()=>{
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            setMyPosts(snapshot.docs.map(doc => {
                if(activeUser && doc.data().username.toLowerCase() === activeUser.toLowerCase()){
                    // console.log(doc.data().timestamp.seconds)
                    return doc.data();
                }else{
                    return null;
                }
            })))        

    },[activeUser])

    const Delete = (id)=>{

        db.collection('posts').doc(id).delete()
    }

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
      };
    return (
        <div className='my_posts_container'>
        {myPosts && myPosts.map((id,post) =>{
            if(post !== null){
                date = '';
                item = 0
                date = new Date(post.timestamp.seconds);
                
                
                 
                return(

               
            <div className="my_post" key={id}>
                <div className="post_header">
                    <div className="right">
                    <Avatar 
                            alt={post.username ? post.username : ''}  
                            className='avatar'
                            src="/static/images/avatar/1.jpg"
                            />
                        <h3>{post.username}</h3>
                    </div>
                    <div className="left">
                        <div className="doc">
                        <i className="fas fa-ellipsis-v"
                        onClick={()=> setOn(true)}></i>
                        </div>
                    </div>
                    <div className={on === true ? "operations on" : "operations"}>
                        <div>Update</div>
                        <div onClick={()=> Delete("1")}>Delete</div>
                    </div>
                </div>
                <div className="post_image">
                    <Slider {...settings}>
                    {Object.keys(post.imagesUrl).length !== 0 && post.imagesUrl.map((image,i) =>{
                        return(
                            <img key={i} src={image} alt="" />
                        )
                    })}
                    
                    
                    </Slider>
                </div>
                <div className="post_date">
                    <p>
                    {date.getMonth() + 1}/{date.getDate()}/{date.getFullYear()} || {date.getHours()}:{date.getMinutes()}  
                    {id}</p>
                    
                </div>
            </div>
            )
            }
        })}
            
        </div>
    )
}

export default MyPosts
