import React,{useState,useEffect,useContext} from 'react';
import './clientRoom.css';
import {db} from '../../Firebase';
import {SideBarContext} from '../../contexts/SideBarContext';


function ClientRoom() {
    
    
    const {value2} = useContext(SideBarContext);
    const [activeUser,setActiveUser] = value2;
    const [messages,setMessages] = useState([])
    const room = []
    let name = null;
    const getMessages = async()=>{
        
        db.collection('posts').orderBy('timestamp','asc')
        .onSnapshot(snapshot => 
            snapshot.docs.map(doc => {
                db.collection('posts').doc(doc.id).collection('rooms')
                .orderBy('timestamp','asc')
                .onSnapshot(snapshot => 
                snapshot.docs.map(doc => 
                    { 
                        name = doc.data().receiver
                        
                        if(name && name?.toLowerCase() === activeUser?.toLowerCase()){
                            room.push(doc.data())
                            
                            setMessages([...room])
                        }
                            

                        
                       
                    }))
                
                return ()=> messages
                
            })
        )

    }

useEffect(()=>{

    getMessages();
    return ()=> setMessages([])
    
},[])
    

    

    return (
        <div className='messages_container'>
           <button onClick={(e)=>{
               e.preventDefault()
               console.log(messages)
            
           }}>click</button> 
               
            {messages.map((msg,id)=>{
                return(
                    <div key={id}>
                        {msg.message}
                    </div>
                )
            })}
               
           
           


           
        </div>
    )
}

export default ClientRoom
